import React, { Fragment, useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  getRecipes,
  filterRecipesbyDiets,
  orderByName,
  orderByScore,
  filterMayorCincuenta,
} from "../../actions";

import Card from "../Card/Card";
import Paginado from "../Paginado/Paginado";
import NavBar from "../NavBar/NavBar";
import "./Home.css";

//HOME SERA UN COMPONENTE FUNCIONAL
export default function Home() {
  const dispatch = useDispatch();
  const allRecipes = useSelector((state) => state.recipes);
  const recetas = useSelector((state) => state.allRecipes);
  const notFound = useSelector((state) => state.error);

  //---------- PAGINADO ----------

  const [currentPage, setCurrentPage] = useState(1); //estado local para la pag actual
  const [recipesPerPage /*setrecipesPerPage*/] = useState(9); //estado para guardar la cantidad de rec x page
  const indexOfLastRecipe = currentPage * recipesPerPage; //9
  const indexOfFirtsRecipe = indexOfLastRecipe - recipesPerPage; //0
  const currentRecipe = allRecipes.slice(indexOfFirtsRecipe, indexOfLastRecipe);

  const paginado = (pageNumber) => {
    setCurrentPage(pageNumber); //cambia mi numero de pagina
  };

  const [, /*orden*/ setOrden] = useState(""); //lo uso solo para modificar y render

  //nos traemos las recetas del estado cuando se monta
  useEffect(() => {
    dispatch(getRecipes());
  }, [dispatch]);

  //--------- HANDLERS ---------
  function handleFilterRecipes(e) {
    dispatch(filterRecipesbyDiets(e.target.value));
    setCurrentPage(1);
  }

  function handleOrderByName(e) {
    e.preventDefault();
    dispatch(orderByName(e.target.value));
    setCurrentPage(1);
    setOrden(e.target.value);
  }

  function handleOrderByScore(e) {
    e.preventDefault();
    dispatch(orderByScore(e.target.value));
    setCurrentPage(1);
    setOrden(e.target.value);
  }


  //filter mayor de 50
  function handleFilterMayorCincuenta(e) {
    e.preventDefault();
    dispatch(filterMayorCincuenta(e.target.value));
    setCurrentPage(1);
  }

  const handlePrevPage = (e) => {
    e.preventDefault()
    if (currentPage === 1) {
      setCurrentPage(currentPage - 1)
      setRecipesPerPage(9)
    }
  }

  const handleNextPage = (e) => {
    e.preventDefault()
    if (currentPage < Math.ceil(allRecipes.length / 9)) {
      setCurrentPage(currentPage + 1)
      recipesPerPage(9)
    }
  }

  return (
    <div className="home">
      <NavBar />
      <div>
        <div className="filtros">
          <select
            className="filtro1"
            defaultValue="sortByName"
            onChange={(e) => handleOrderByName(e)}
          >
            <option value="sortByName" select disabled>
              Sort By Name
            </option>
            <option value="asc">A-Z</option>
            <option value="desc">Z-A</option>
          </select>
          <select
            className="filtro2"
            defaultValue="sortByScore"
            onChange={(e) => handleOrderByScore(e)}
          >
            <option value="sortByScore" select disabled>
              Sort By Score
            </option>
            <option value="high">Highest score</option>
            <option value="low">Lowest score</option>
          </select>

          <select
            className="filtro3"
            defaultValue="all"
            onChange={(e) => handleFilterRecipes(e)}
          >
            <option value="all">All</option>
            <option value="dairy free">Dairy free</option>
            <option value="gluten free">Gluten free</option>
            <option value="lacto ovo vegetarian">Lacto-ovovegetarian</option>
            <option value="fodmap friendly">Low fodmap</option>
            <option value="ketogenic">Ketogenic</option>
            <option value="paleolithic">paleolithic</option>
            <option value="pescatarian">Pescatarian</option>
            <option value="primal">Primal</option>
            <option value="vegan">Vegan</option>
            <option value="vegetarian">Vegetarian</option>
            <option value="whole 30">Whole30</option>
          </select>
          <select className="filtro4"
            defaultValue="all" onChange={e => handleFilterMayorCincuenta(e)}>
            <option value='All'>Todas las recetas</option>
            <option value='mayorCin'>Healt Score Mayor 50</option>
            <option value='menorCin'>Healt Score Menor 50</option>

          </select>

        </div>

      </div>
      <div className="paginado">
        <Paginado
          recipesPerPage={recipesPerPage}
          allRecipes={allRecipes.length}
          paginado={paginado}
          handleNextPage={handleNextPage}
          handlePrevPage={handlePrevPage}
        />
      </div>

      <div>
        {recetas.length === 0 ? (
          <div>
            <p id="not_found2"> </p>
            <img
              alt="loading"
              src=" https://media3.giphy.com/media/M2kCXo31seejZyuCMZ/giphy.gif?cid=ecf05e4742klc07rcztt5vy9faupcpbozevdfpwwhgei3si0&rid=giphy.gif&ct=s"
              id="img"
            />
          </div>
        ) : notFound.length === 0 && allRecipes.length > 0 ? (
          <div className="card_container">
            {currentRecipe?.map((e) => {
              return (
                <Fragment>
                  <Card
                    title={e.title}
                    image={e.image}
                    diets={e.diets.join(", ")}
                    id={e.id}
                    key={e.id}
                  />
                </Fragment>
              );
            })}
          </div>
        ) : (
          <p id="not_found">Not recipe found</p>
        )}
      </div>
    </div>

  );
}
