import React from "react";
import "./Paginado.css";

export default function Paginado({ recipesPerPage, allRecipes, paginado, handlePrevPage, handleNextPage }) {
  const pageNumber = [];

  for (let i = 1; i <= Math.ceil(allRecipes / recipesPerPage); i++) {//math.ceil redondea pa arriba
    pageNumber.push(i);
  }
  return (
    <nav>
      <div className="paginado">
        <button className="flecha" onClick={(e) => handlePrevPage(e)}>{'<'}</button>

        <button className="flecha" onClick={(e) => handleNextPage(e)}>{'>'}</button>
        {pageNumber &&
          pageNumber.map((number) => (

            <button
              className="number"
              key={number}
              onClick={() => paginado(number)}
            >
              {number}
            </button>
          ))}
      </div>
    </nav>
  );
}
